import { Text} from 'react-native';   

export default function MealDetailScreen({ route }) {
    const mealId = route.params.mealId;

    const selectedMeal = MEALS.find((meal) => meal.id === mealId)


    return (
        <View>
            <Image source={{uri: selectedMeal.item.image}} />
        </View>
    )
}
